import React, { useState, useEffect } from "react";
import './styles.scss'; 
import logo from '../../assets/images/logo.png';

const Home = () => {
  const lines = ["Let's Connect", "Hi, I'm Ihsana - a passionate coder!"];
  const [displayedText, setDisplayedText] = useState("");
  const [currentLine, setCurrentLine] = useState(0);
  const speed = 100; // Speed of the typewriting effect
  const pauseBetweenLines = 1000; // Pause before starting the next line

  useEffect(() => {
    let index = 0;
    let interval;

    const typeLine = () => {
      interval = setInterval(() => {
        if (index < lines[currentLine].length) {
          setDisplayedText((prevText) => prevText + lines[currentLine][index]);
          index += 1;
        } else {
          clearInterval(interval);
          if (currentLine < lines.length - 1) {
            setTimeout(() => {
              setDisplayedText(""); // Clear the text for the next line
              setCurrentLine((prevLine) => prevLine + 1); // Move to the next line
            }, pauseBetweenLines);
          }
        }
      }, speed);
    };

    typeLine();

    // Clear the interval when the component unmounts or currentLine changes
    return () => clearInterval(interval);
  }, [currentLine, lines]);

  return (
    <div className="home-container">
      <div className="home-content">
        <h1>{displayedText}</h1>
      </div>
      <div className="home-image1">
        <img src={logo} alt="image1" />
      </div>
    </div>
  );
};

export default Home;
