export const particlesConfig = {
    background: {
        color: {
            value: "#000", // Set the background color of the particles canvas
        },
    },
    fpsLimit: 60, // Limit the FPS to 60
    interactivity: {
        events: {
            onClick: {
                enable: true,
                mode: "push", // Add particles on click
            },
            onHover: {
                enable: true,
                mode: "repulse", // Repulse particles on hover
            },
            resize: true,
        },
        modes: {
            push: {
                quantity: 4,
            },
            repulse: {
                distance: 200,
                duration: 0.4,
            },
        },
    },
    particles: {
        color: {
            value: "#ffffff", // Particles color
        },
        links: {
            color: "#ffffff", // Line color between particles
            distance: 150, // Distance for connecting particles
            enable: true, // Enable linking particles
            opacity: 0.5,
            width: 1,
        },
        collisions: {
            enable: true, // Enable collisions between particles
        },
        move: {
            direction: "none", // Move direction of particles
            enable: true, // Enable movement
            outModes: {
                default: "bounce",
            },
            random: false,
            speed: 2, // Speed of movement
            straight: false,
        },
        number: {
            density: {
                enable: true,
                area: 800, // Control particle density
            },
            value: 50, // Number of particles
        },
        opacity: {
            value: 0.5,
        },
        shape: {
            type: "circle", // Shape of particles
        },
        size: {
            value: { min: 10, max: 20 }, // Particle size range
        },
    },
    detectRetina: true, // Enable retina detection
};
